import React, {useState} from 'react';
import axios from 'axios';
import Station from './Station';
import './Menu.css';
// import Reservation from './Reservation';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';

const columns = [
    { id: 'name', label: '구분', minWidth: 170 },
    { id: 'code', label: '도착시간', minWidth: 100 },
    {
      id: 'population',
      label: '열차번호',
      minWidth: 170,
      align: 'right',
      format: value => value.toLocaleString(),
    },
    {
      id: 'size',
      label: '출발시간',
      minWidth: 170,
      align: 'right',
      format: value => value.toLocaleString(),
    },
];

function createData(name, code, population, size) {
    const density = population / size;
    return { name, code, population, size, density };
}

const rows = [];

const useStyles = makeStyles({
    root: {
      width: '100%',
    },
    container: {
      maxHeight: 440,
    },
});

const Menu = () => {

    const [from, setFrom] = useState();
    const [to, setTo] = useState();
    console.log("from:",from);
    console.log("to:",to);
    const [tdata,setData] = useState();

    const classes = useStyles();
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);

    const handleChangePage = (event, newPage) => {
      setPage(newPage);
    };

    const handleChangeRowsPerPage = event => {
      setRowsPerPage(+event.target.value);
      setPage(0);
    };

    const onClick = async () => {

        // let frd = new FormData();

        // frd.append('date', '20200202');
        // frd.append('hour', '160000');
        // frd.append('start', '서울');
        // frd.append('end', '부산');

        // console.log(frd)

        try {
            const response = await axios.get('http://15.165.170.3:8000/info/tickets/' , {
                    headers : { "Content-Type": "multipart/form-data"} ,
                    params : { 
                                date : '20200225',
                                hour : '160000',
                                start : '김천',
                                end : '부산'

                            }
                });
            setData(response.data.tickets)


            // const body = { date: '20200220', hour: '160000', start:'서울', end:'부산' };
            // axios({
            //     url:'http://15.165.170.3:8000/info/tickets/',
            //     method:'GET',
            //     headers:{
            //     'Content-Type':'multipart/form-data'
            //     },
            //     data:body,
            // })
            // setData(response);
        } catch (e) {
            console.log(e);
        }

    };


    return (
        <>
            <div className = "menu-wrapper">
                <fieldlist className = "station-wrapper">
                    <legend className = "menu-title">승차권 예매하기</legend>
                    <ul className = "menu-ul">
                        <li className = "li-wrapper">
                            <label className = "menu-leg"> 출발역 </label>
                            <Station setStation = {setFrom} station = {from}></Station>
                        </li>
                    </ul>
                    <ul>
                        <li className = "li-wrapper">
                            <label className = "menu-leg"> 도착역 </label>
                            <Station setStation = {setTo} station = {to}></Station>
                        </li>
                    </ul>
                    <ul>
                        <li className = "li-wrapper">
                            <label className = "date-wrapper">출발일 </label>
                            <input className = "menu-input-date" type = "date" name = "date"/>
                        </li>
                    </ul>
                    <ul>
                        <li className= "li-wrapper">
                            <label className = "time-wrapper">시간</label>
                            <input className = "menu-input-time" type = "time" name = "time"/>
                        </li>
                    </ul>
                    <div>
                        <button onClick={onClick}>기차 검색하기</button>
                    </div>
                    <div>
                        {tdata && <textarea rows={7} value = {JSON.stringify(tdata,null,2)}/>}
                    </div>
                </fieldlist>
            </div>
            <div>
                        <Paper className={classes.root}>
                            <TableContainer className={classes.container}>
                                <Table stickyHeader aria-label="sticky table">
                                <TableHead>
                                    <TableRow>
                                    {columns.map(column => (
                                        <TableCell
                                        key={column.id}
                                        align={column.align}
                                        style={{ minWidth: column.minWidth }}
                                        >
                                        {column.label}
                                        </TableCell>
                                    ))}
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map(row => {
                                    return (
                                        <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                                        {columns.map(column => {
                                            const value = row[column.id];
                                            return (
                                            <TableCell key={column.id} align={column.align}>
                                                {column.format && typeof value === 'number' ? column.format(value) : value}
                                            </TableCell>
                                            );
                                        })}
                                        </TableRow>
                                    );
                                    })}
                                </TableBody>
                                </Table>
                            </TableContainer>
                            <TablePagination
                                rowsPerPageOptions={[10, 25, 100]}
                                component="div"
                                count={rows.length}
                                rowsPerPage={rowsPerPage}
                                page={page}
                                onChangePage={handleChangePage}
                                onChangeRowsPerPage={handleChangeRowsPerPage}
                            />
                            </Paper>
                        </div>

        </>
    )
}

export default Menu;
