import React, {useState} from 'react';
import axios from 'axios';
import Station from './Station';
import './Menu.css';
// import Reservation from './Reservation';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const StyledTableCell = withStyles(theme => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles(theme => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.background.default,
    },
  },
}))(TableRow);

function createData(name, calories, fat, carbs) {
  return { name, calories, fat, carbs};
}

var rows = [];

const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
});


const Menu = () => {

    const [from, setFrom] = useState();
    const [to, setTo] = useState();
    console.log("from:",from);
    console.log("to:",to);
    const [tdata,setData] = useState();

    const classes = useStyles();



    const onClick = async () => {

        // let frd = new FormData();

        // frd.append('date', '20200202');
        // frd.append('hour', '160000');
        // frd.append('start', '서울');
        // frd.append('end', '부산');

        // console.log(frd)

        try {
            const response = await axios.get('http://15.165.170.3:8000/info/tickets/' , {
                    headers : { "Content-Type": "multipart/form-data"} ,
                    params : { 
                                date : '20200225',
                                hour : '160000',
                                start : '김천',
                                end : '부산'

                            }
                });
            setData(response.data.tickets)
            // rows.append()
            var temp = []
            for(var i in tdata) {
                console.log(tdata[i])
                temp = temp.concat(createData(tdata[i].구분, tdata[i].출발시간, tdata[i].열차번호, tdata[i].도착시간))
                console.log(createData(tdata[i].구분, tdata[i].출발시간, tdata[i].열차번호, tdata[i].도착시간))
            }
            console.log(temp)


            // const body = { date: '20200220', hour: '160000', start:'서울', end:'부산' };
            // axios({
            //     url:'http://15.165.170.3:8000/info/tickets/',
            //     method:'GET',
            //     headers:{
            //     'Content-Type':'multipart/form-data'
            //     },
            //     data:body,
            // })
            // setData(response);
        } catch (e) {
            console.log(e);
        }

    };


    return (
        <>
            <div className = "menu-wrapper">
                <fieldlist className = "station-wrapper">
                    <legend className = "menu-title">승차권 예매하기</legend>
                    <ul className = "menu-ul">
                        <li className = "li-wrapper">
                            <label className = "menu-leg"> 출발역 </label>
                            <Station setStation = {setFrom} station = {from}></Station>
                        </li>
                    </ul>
                    <ul>
                        <li className = "li-wrapper">
                            <label className = "menu-leg"> 도착역 </label>
                            <Station setStation = {setTo} station = {to}></Station>
                        </li>
                    </ul>
                    <ul>
                        <li className = "li-wrapper">
                            <label className = "date-wrapper">출발일 </label>
                            <input className = "menu-input-date" type = "date" name = "date"/>
                        </li>
                    </ul>
                    <ul>
                        <li className= "li-wrapper">
                            <label className = "time-wrapper">시간</label>
                            <input className = "menu-input-time" type = "time" name = "time"/>
                        </li>
                    </ul>
                    <div>
                        <button onClick={onClick}>기차 검색하기</button>
                    </div>
                    <div>
                        {tdata && <textarea rows={7} value = {JSON.stringify(tdata,null,2)}/>}
                    </div>
                </fieldlist>
            </div>
            <div>
            <TableContainer component={Paper}>
                <Table className={classes.table} aria-label="customized table">
                    <TableHead>
                    <TableRow>
                        <StyledTableCell>Dessert (100g serving)</StyledTableCell>
                        <StyledTableCell align="right">Calories</StyledTableCell>
                        <StyledTableCell align="right">Fat&nbsp;(g)</StyledTableCell>
                        <StyledTableCell align="right">Carbs&nbsp;(g)</StyledTableCell>
                    </TableRow>
                    </TableHead>
                    <TableBody>
                    {rows.map(row => (
                        <StyledTableRow key={row.name}>
                            <StyledTableCell component="th" scope="row">
                                {row.name}
                            </StyledTableCell>
                            <StyledTableCell align="right">{row.calories}</StyledTableCell>
                            <StyledTableCell align="right">{row.fat}</StyledTableCell>
                            <StyledTableCell align="right">{row.carbs}</StyledTableCell>
                        </StyledTableRow>
                    ))}
                    </TableBody>
                </Table>
            </TableContainer>
            </div>
        </>
    )
}

export default Menu;
